
public class Partetres {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int X=20;
         int Y=30;
         double N=40.22;
         double M=14.67;
         System.out.println("X es igual a: " +X +" - Y es igual a: " +Y +" - N es igual a: " +N +" - M es igual a: " +M);
         System.out.println("X+Y es igual a: " +(X+Y));
         System.out.println("La diferencia entre X e Y es igual a: " +(X-Y));
         System.out.println("El producto entre X e Y es igual a: " +(X*Y));
         System.out.println("El cocientre entre X e Y es igual a: " +(X/Y));
         System.out.println("El módulo de X/Y es igual a: " +(X%Y));
         System.out.println("N+M es igual a: " +(N+M));
         System.out.println("La resta de N-M es igual a: " +(N-M));
         System.out.println("El producto de N y M es igual a: " +(N*M));
         System.out.println("El cocientre entre N y M es igual a: " +(N/M));
         System.out.println("El módulo de N y M es igual a: " +(N%M));
         System.out.println("X+N es igual a: " +(X+N));
         System.out.println("El cociente entre Y y M es igual a: " +(Y/M));
         System.out.println("El módulo de Y y M es igual a: " +(Y%M));
         System.out.println("2X es igual a: " +(X*2) +" - 2Y es igual a: " +(Y*2) +" - 2N es igual a: " +(N*2) +" - 2M es igual a: " +(M*2));
         System.out.println("X+Y+N+M es igual a: " +(X+Y+N+M));
         System.out.println("El producto entre todas las variables es igual a: " +(X*Y*M*N));
	}

}
